<script>
import { useStore } from "vuex";
import { useRoute } from "vue-router";
import contactService from "../services/contact.service";
export default {
  setup() {
    const route = useRoute();
    const store = useStore();
    var parkLocal ;
    var AreaId;
    if(route.params.name=="admin"){
      store.commit('login',{user_id:0})
    }else {store.commit('login',{user_id:route.params.id})}
    
    store.commit('userRent',{user:route.params})
    const now = new Date()
    const time = new Date()
    store.state.Area.forEach((e)=>{
      e.park.forEach((park)=>{
        if(park.user.id == route.params.id ){
          AreaId = e._id
          parkLocal= park;
          time.setTime(park.user.time)
        }
      })
    })
    
    now.getTime()
    const totalTime = ((((now-time)/1000)/60)/60)
    // function
    async function unRent() {
      if(AreaId){

      if(parkLocal.user.phone){
        await new contactService("/api/user").delete(route.params.id)
      }
      parkLocal.rented = false;
      parkLocal.user = {}
      await new contactService("/api").update(AreaId,parkLocal)
      }
    }
    return { time,totalTime,unRent }
  },
};
</script>

